/* App.js */
import { Navbar, Button, Nav, Form, FormControl } from "react-bootstrap";
import ReactDOM from "react-dom";
var React = require("react");
var Component = React.Component;
var CanvasJSReact = require("./canvasjs.react").default;
var CanvasJS = CanvasJSReact.CanvasJS;
var CanvasJSChart = CanvasJSReact.CanvasJSChart;
var datapoints_one = [];
var datapoints_two = [];

class Home extends Component {
  render() {
    const options = {
      animationEnabled: true,
      title: {
        text: "Number of New Customers",
      },
      axisY: {
        title: "Number of Customers",
      },
      toolTip: {
        shared: true,
      },
      data: [
        {
          name: "2016",
          showInLegend: true,
          type: "line",

          xValueFormatString: "MMM YYYY",
          yValueFormatString: "$#,##0.00",
          datapoints_one: datapoints_one,
        },
        {
          type: "spline",
          name: "2017",
          type: "line",

          showInLegend: true,
          datapoints_two: datapoints_two,
        },
      ],
    };
    return (
      <div className="home-page">
        <Navbar className="color-nav" variant="dark">
          <Navbar.Brand href="#home">Navbar</Navbar.Brand>
          <Nav className="mr-auto">
            <Nav.Link href="#home">Home</Nav.Link>
            <Nav.Link href="#features">About</Nav.Link>
          </Nav>
        </Navbar>
        <br />

        <div className="product-form">
          <Form>
            <Form.Group controlId="formBasicEmail">
              <Form.Label>Enter the amazon URL of product</Form.Label>
              <Form.Control placeholder="Amazon URL" />
            </Form.Group>
            <Form.Group controlId="formBasicEmail">
              <Form.Label>Enter the Flipkart URL of product</Form.Label>
              <Form.Control placeholder="Flipkart URL" />
            </Form.Group>

            <Button variant="primary" type="submit">
              Submit
            </Button>
          </Form>
        </div>
        <div className="graph">
          <div>
            <CanvasJSChart
              options={options}
              /* onRef={ref => this.chart = ref} */
            />
            {/*You can get reference to the chart instance as shown above using onRef. This allows you to access all chart properties and methods*/}
          </div>
        </div>
      </div>
    );
  }

  componentDidMount() {
    var chart = this.chart;
    fetch("https://canvasjs.com/data/gallery/react/nifty-stock-price.json")
      .then(function (response) {
        return response.json();
      })
      .then(function (data) {
        for (var i = 0; i < data.length; i++) {
          datapoints_one.push({
            label: new Date(data[i].x),
            y: data[i].y,
          });
          datapoints_two.push({
            label: new Date(data[i].x),
            y: data[i].y,
          });
        }
        chart.render();
      });
  }
}

export default Home;
